import React from 'react';
import { motion } from 'framer-motion';
import CoverLetterGenerator from './components/CoverLetterGenerator';
import './App.css';

function App() {
  return (
    <div className="min-h-screen bg-gradient-to-br from-[#e0f2fe] via-white to-[#ede9fe] text-gray-800">
      {/* Header */}
      <header className="bg-white/80 backdrop-blur-sm shadow-md border-b border-gray-200 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <h1 className="text-3xl font-extrabold text-indigo-700 tracking-tight">
            AI Cover Letter Generator
          </h1>
          <span className="text-sm text-gray-500">⚡ Powered by Gemini AI</span>
        </div>
      </header>

      {/* Hero Section */}
      <motion.section
        className="py-16 px-6 text-center bg-gradient-to-tr from-indigo-50 to-white"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6, ease: 'easeOut' }}
      >
        <h2 className="text-4xl font-bold text-indigo-900 mb-4">
          Craft Personalized Cover Letters in Seconds
        </h2>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto mb-6">
          Save time and stand out. Let AI help you generate customized, professional cover letters effortlessly.
        </p>
        <motion.a
          href="#generator"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.97 }}
          className="inline-block px-6 py-3 bg-indigo-600 text-white rounded-full font-semibold hover:bg-indigo-700 transition"
        >
          Get Started
        </motion.a>
      </motion.section>

      {/* Main Generator Section */}
      <motion.main
        id="generator"
        className="max-w-4xl mx-auto py-12 px-6"
        initial={{ opacity: 0, scale: 0.95 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, ease: 'easeOut' }}
        viewport={{ once: true }}
      >
        <motion.div
          className="bg-white rounded-2xl shadow-xl p-8"
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, ease: 'easeOut' }}
        >
          <h3 className="text-2xl font-semibold text-indigo-800 mb-4 text-center">
            Generate Your Cover Letter
          </h3>
          <CoverLetterGenerator />
        </motion.div>
      </motion.main>

      {/* Footer */}
      <footer className="bg-white/80 backdrop-blur-sm border-t border-gray-200 mt-20">
        <div className="max-w-7xl mx-auto py-6 px-4 text-center text-sm text-gray-500">
          © 2024 AI Cover Letter Generator. Built with React, Tailwind & Gemini AI.
        </div>
      </footer>
    </div>
  );
}

export default App;
