import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

const CoverLetterGenerator = () => {
    const [resumeFile, setResumeFile] = useState(null);
    const [jobDescription, setJobDescription] = useState('');
    const [templateStyle, setTemplateStyle] = useState('professional');
    const [customMessage, setCustomMessage] = useState('');
    const [coverLetter, setCoverLetter] = useState('');
    const [analysis, setAnalysis] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [templates, setTemplates] = useState({});
    const [activeTab, setActiveTab] = useState('generate');

    // Get API URL from environment
    const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5051';

    // Load templates on component mount
    React.useEffect(() => {
        loadTemplates();
    }, []);

    const loadTemplates = async () => {
        try {
            const response = await axios.get(`${API_URL}/api/templates`);
            if (response.data.success) {
                setTemplates(response.data.templates);
            }
        } catch (error) {
            console.error('Failed to load templates:', error);
        }
    };

    // File dropzone configuration
    const onDrop = useCallback((acceptedFiles) => {
        const file = acceptedFiles[0];
        if (file) {
            // Validate file size (16MB)
            const maxSize = parseInt(process.env.REACT_APP_MAX_FILE_SIZE) || 16777216;
            if (file.size > maxSize) {
                setError('File size too large. Maximum size is 16MB.');
                return;
            }
            setResumeFile(file);
            setError('');
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: {
            'application/pdf': ['.pdf'],
            'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
            'application/msword': ['.doc'],
            'text/plain': ['.txt']
        },
        multiple: false
    });

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!resumeFile) {
            setError('Please upload a resume file.');
            return;
        }

        if (!jobDescription.trim()) {
            setError('Please provide a job description.');
            return;
        }

        setLoading(true);
        setError('');

        const formData = new FormData();
        formData.append('resume', resumeFile);
        formData.append('job_description', jobDescription);
        formData.append('template_style', templateStyle);
        if (customMessage.trim()) {
            formData.append('custom_message', customMessage);
        }

        try {
            console.log('Sending request data:', requestData);
            const response = await axios.post(
                `${API_URL}/api/cover_letter_generator`,
                formData,
                { 
                    headers: { 
                        'Content-Type': 'multipart/form-data' 
                    },
                    timeout: 60000 // 60 second timeout
                }
            );

            if (response.data.success) {
                setCoverLetter(response.data.cover_letter);
                setAnalysis(response.data.analysis);
                setMetadata(response.data.metadata);
                setActiveTab('result');
            } else {
                setError(response.data.error || 'Failed to generate cover letter');
            }
        } catch (error) {
            console.error('Error:', error);
            if (error.response?.data?.error) {
                setError(error.response.data.error);
            } else if (error.code === 'ECONNABORTED') {
                setError('Request timed out. Please try again.');
            } else {
                setError('An error occurred. Please check your connection and try again.');
            }
        } finally {
            setLoading(false);
        }
    };

    const copyToClipboard = async () => {
        try {
            await navigator.clipboard.writeText(coverLetter);
            // You could add a toast notification here
            alert('Cover letter copied to clipboard!');
        } catch (err) {
            console.error('Failed to copy text: ', err);
        }
    };

    const downloadCoverLetter = () => {
        const element = document.createElement('a');
        const file = new Blob([coverLetter], { type: 'text/plain' });
        element.href = URL.createObjectURL(file);
        element.download = 'cover-letter.txt';
        document.body.appendChild(element);
        element.click();
        document.body.removeChild(element);
    };

    const resetForm = () => {
        setResumeFile(null);
        setJobDescription('');
        setCustomMessage('');
        setCoverLetter('');
        setAnalysis(null);
        setMetadata(null);
        setError('');
        setActiveTab('generate');
    };

    return (
        <div className="max-w-6xl mx-auto p-6">
            {/* Header */}
            <div className="text-center mb-8">
                <h1 className="text-4xl font-bold text-gray-900 mb-4">
                    AI-Powered Cover Letter Generator
                </h1>
                <p className="text-lg text-gray-600 max-w-2xl mx-auto">
                    Upload your resume and job description to generate a personalized, 
                    professional cover letter in seconds using advanced AI technology.
                </p>
            </div>

            {/* Tabs */}
            <div className="mb-6">
                <div className="border-b border-gray-200">
                    <nav className="-mb-px flex space-x-8">
                        <button
                            onClick={() => setActiveTab('generate')}
                            className={`py-2 px-1 border-b-2 font-medium text-sm ${
                                activeTab === 'generate'
                                    ? 'border-blue-500 text-blue-600'
                                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                            }`}
                        >
                            Generate Cover Letter
                        </button>
                        {coverLetter && (
                            <button
                                onClick={() => setActiveTab('result')}
                                className={`py-2 px-1 border-b-2 font-medium text-sm ${
                                    activeTab === 'result'
                                        ? 'border-blue-500 text-blue-600'
                                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                                }`}
                            >
                                View Result
                            </button>
                        )}
                        {analysis && (
                            <button
                                onClick={() => setActiveTab('analysis')}
                                className={`py-2 px-1 border-b-2 font-medium text-sm ${
                                    activeTab === 'analysis'
                                        ? 'border-blue-500 text-blue-600'
                                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                                }`}
                            >
                                Skills Analysis
                            </button>
                        )}
                    </nav>
                </div>
            </div>

            {/* Error Message */}
            {error && (
                <div className="mb-6 bg-red-50 border border-red-200 rounded-md p-4">
                    <div className="flex">
                        <div className="flex-shrink-0">
                            <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
                            </svg>
                        </div>
                        <div className="ml-3">
                            <p className="text-sm text-red-800">{error}</p>
                        </div>
                    </div>
                </div>
            )}

            {/* Generate Tab */}
            {activeTab === 'generate' && (
                <div className="bg-white rounded-lg shadow-md p-6">
                    <form onSubmit={handleSubmit} className="space-y-6">
                        {/* Resume Upload */}
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Upload Resume
                            </label>
                            <div
                                {...getRootProps()}
                                className={`border-2 border-dashed rounded-lg p-6 text-center cursor-pointer transition-colors ${
                                    isDragActive
                                        ? 'border-blue-400 bg-blue-50'
                                        : resumeFile
                                        ? 'border-green-400 bg-green-50'
                                        : 'border-gray-300 hover:border-gray-400'
                                }`}
                            >
                                <input {...getInputProps()} />
                                <div className="space-y-2">
                                    <svg className="mx-auto h-12 w-12 text-gray-400" stroke="currentColor" fill="none" viewBox="0 0 48 48">
                                        <path d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
                                    </svg>
                                    {resumeFile ? (
                                        <div>
                                            <p className="text-sm text-green-600 font-medium">
                                                ✓ {resumeFile.name}
                                            </p>
                                            <p className="text-xs text-gray-500">
                                                ({(resumeFile.size / 1024 / 1024).toFixed(2)} MB)
                                            </p>
                                        </div>
                                    ) : isDragActive ? (
                                        <p className="text-sm text-blue-600">Drop the file here...</p>
                                    ) : (
                                        <div>
                                            <p className="text-sm text-gray-600">
                                                <span className="font-medium">Click to upload</span> or drag and drop
                                            </p>
                                            <p className="text-xs text-gray-500">
                                                PDF, DOCX, DOC, or TXT (max 16MB)
                                            </p>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Job Description */}
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Job Description
                            </label>
                            <textarea
                                value={jobDescription}
                                onChange={(e) => setJobDescription(e.target.value)}
                                rows={12}
                                className="w-full border border-gray-300 rounded-md p-3 focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                                placeholder="Paste the complete job description here including requirements, responsibilities, and company information..."
                                required
                            />
                            <div className="mt-1 text-xs text-gray-500">
                                {jobDescription.length} characters (minimum 50 recommended)
                            </div>
                        </div>

                        {/* Template Selection */}
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Cover Letter Style
                            </label>
                            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                                {Object.entries(templates).map(([key, template]) => (
                                    <div
                                        key={key}
                                        className={`border rounded-lg p-4 cursor-pointer transition-colors ${
                                            templateStyle === key
                                                ? 'border-blue-500 bg-blue-50 ring-2 ring-blue-200'
                                                : 'border-gray-200 hover:border-gray-300'
                                        }`}
                                        onClick={() => setTemplateStyle(key)}
                                    >
                                        <div className="flex items-center mb-2">
                                            <input
                                                type="radio"
                                                name="template"
                                                value={key}
                                                checked={templateStyle === key}
                                                onChange={() => setTemplateStyle(key)}
                                                className="h-4 w-4 text-blue-600 border-gray-300 focus:ring-blue-500"
                                            />
                                            <label className="ml-2 text-sm font-medium text-gray-900">
                                                {template.name}
                                            </label>
                                        </div>
                                        <p className="text-xs text-gray-600 mb-1">{template.description}</p>
                                        <p className="text-xs text-blue-600 font-medium">
                                            Best for: {template.best_for}
                                        </p>
                                    </div>
                                ))}
                            </div>
                        </div>

                        {/* Custom Message */}
                        <div>
                            <label className="block text-sm font-medium text-gray-700 mb-2">
                                Custom Message (Optional)
                            </label>
                            <textarea
                                value={customMessage}
                                onChange={(e) => setCustomMessage(e.target.value)}
                                rows={3}
                                className="w-full border border-gray-300 rounded-md p-3 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                                placeholder="Add any specific points you'd like to include in your cover letter..."
                            />
                        </div>

                        {/* Submit Button */}
                        <div className="flex justify-between items-center">
                            <button
                                type="button"
                                onClick={resetForm}
                                className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 border border-gray-300 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
                            >
                                Reset Form
                            </button>
                            <button
                                type="submit"
                                disabled={loading || !resumeFile || !jobDescription.trim()}
                                className="px-6 py-3 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                            >
                                {loading ? (
                                    <>
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        Generating...
                                    </>
                                ) : (
                                    'Generate Cover Letter'
                                )}
                            </button>
                        </div>
                    </form>
                </div>
            )}

            {/* Results Tab */}
            {activeTab === 'result' && coverLetter && (
                <div className="bg-white rounded-lg shadow-md p-6">
                    <div className="flex justify-between items-center mb-6">
                        <h2 className="text-2xl font-bold text-gray-900">Generated Cover Letter</h2>
                        <div className="flex space-x-3">
                            <button
                                onClick={copyToClipboard}
                                className="px-4 py-2 bg-green-600 text-white text-sm font-medium rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                            >
                                Copy to Clipboard
                            </button>
                            <button
                                onClick={downloadCoverLetter}
                                className="px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                            >
                                Download TXT
                            </button>
                        </div>
                    </div>

                    {/* Metadata */}
                    {metadata && (
                        <div className="mb-6 p-4 bg-gray-50 rounded-md">
                            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                                <div>
                                    <span className="font-medium text-gray-700">Template:</span>
                                    <span className="ml-1 text-gray-600 capitalize">{metadata.template_used}</span>
                                </div>
                                <div>
                                    <span className="font-medium text-gray-700">Word Count:</span>
                                    <span className="ml-1 text-gray-600">{metadata.word_count}</span>
                                </div>
                                <div>
                                    <span className="font-medium text-gray-700">Generated:</span>
                                    <span className="ml-1 text-gray-600">
                                        {new Date(metadata.generation_timestamp).toLocaleString()}
                                    </span>
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Cover Letter Text */}
                    <div className="bg-gray-50 border rounded-lg p-6">
                        <div className="whitespace-pre-wrap text-gray-800 leading-relaxed font-serif">
                            {coverLetter}
                        </div>
                    </div>

                    {/* Recommendations */}
                    {metadata?.recommendations && metadata.recommendations.length > 0 && (
                        <div className="mt-6 p-4 bg-blue-50 border border-blue-200 rounded-md">
                            <h3 className="text-sm font-medium text-blue-800 mb-2">Recommendations for Improvement:</h3>
                            <ul className="text-sm text-blue-700 space-y-1">
                                {metadata.recommendations.map((rec, index) => (
                                    <li key={index} className="flex items-start">
                                        <span className="mr-2">•</span>
                                        <span>{rec}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}
                </div>
            )}

            {/* Analysis Tab */}
            {activeTab === 'analysis' && analysis && (
                <div className="bg-white rounded-lg shadow-md p-6">
                    <h2 className="text-2xl font-bold text-gray-900 mb-6">Skills Analysis & Job Match</h2>

                    {/* Skills Matching Score */}
                    <div className="mb-8">
                        <div className="flex items-center justify-between mb-2">
                            <h3 className="text-lg font-semibold text-gray-800">Overall Match Score</h3>
                            <span className={`text-2xl font-bold ${
                                analysis.skills_matching.match_score >= 70 ? 'text-green-600' :
                                analysis.skills_matching.match_score >= 50 ? 'text-yellow-600' : 'text-red-600'
                            }`}>
                                {analysis.skills_matching.match_score}%
                            </span>
                        </div>
                        <div className="w-full bg-gray-200 rounded-full h-3">
                            <div
                                className={`h-3 rounded-full ${
                                    analysis.skills_matching.match_score >= 70 ? 'bg-green-500' :
                                    analysis.skills_matching.match_score >= 50 ? 'bg-yellow-500' : 'bg-red-500'
                                }`}
                                style={{ width: `${analysis.skills_matching.match_score}%` }}
                            ></div>
                        </div>
                        <p className="text-sm text-gray-600 mt-2">
                            {analysis.skills_matching.total_matched} of {analysis.skills_matching.total_required} required skills matched
                        </p>
                    </div>

                    <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
                        {/* Matching Skills */}
                        <div>
                            <h3 className="text-lg font-semibold text-gray-800 mb-4">
                                ✅ Matching Skills ({analysis.skills_matching.matching_skills.length})
                            </h3>
                            <div className="space-y-2 max-h-60 overflow-y-auto">
                                {analysis.skills_matching.matching_skills.map((skill, index) => (
                                    <div key={index} className="flex items-center p-2 bg-green-50 border border-green-200 rounded">
                                        <div className="w-2 h-2 bg-green-500 rounded-full mr-3"></div>
                                        <span className="text-sm text-green-800">{skill}</span>
                                    </div>
                                ))}
                                {analysis.skills_matching.matching_skills.length === 0 && (
                                    <p className="text-sm text-gray-500 italic">No matching skills found</p>
                                )}
                            </div>
                        </div>

                        {/* Missing Skills */}
                        <div>
                            <h3 className="text-lg font-semibold text-gray-800 mb-4">
                                ❌ Missing Skills ({analysis.skills_matching.missing_skills.length})
                            </h3>
                            <div className="space-y-2 max-h-60 overflow-y-auto">
                                {analysis.skills_matching.missing_skills.map((skill, index) => (
                                    <div key={index} className="flex items-center p-2 bg-red-50 border border-red-200 rounded">
                                        <div className="w-2 h-2 bg-red-500 rounded-full mr-3"></div>
                                        <span className="text-sm text-red-800">{skill}</span>
                                    </div>
                                ))}
                                {analysis.skills_matching.missing_skills.length === 0 && (
                                    <p className="text-sm text-green-600 italic">Great! No missing required skills.</p>
                                )}
                            </div>
                        </div>
                    </div>

                    {/* Recommendations */}
                    {analysis.skills_matching.recommendations && (
                        <div className="mt-8 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                            <h3 className="text-lg font-semibold text-blue-800 mb-3">Recommendations</h3>
                            <ul className="space-y-2">
                                {analysis.skills_matching.recommendations.map((rec, index) => (
                                    <li key={index} className="flex items-start text-sm text-blue-700">
                                        <span className="mr-2 mt-1">💡</span>
                                        <span>{rec}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    )}

                    {/* Resume & Job Summary */}
                    <div className="mt-8 grid grid-cols-1 lg:grid-cols-2 gap-6">
                        {/* Resume Summary */}
                        <div className="p-4 bg-gray-50 rounded-lg">
                            <h3 className="text-lg font-semibold text-gray-800 mb-3">Resume Summary</h3>
                            <div className="space-y-2 text-sm">
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Skills Found:</span>
                                    <span className="font-medium">{analysis.resume_data.skills_count}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Experience Entries:</span>
                                    <span className="font-medium">{analysis.resume_data.experience_count}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Education Entries:</span>
                                    <span className="font-medium">{analysis.resume_data.education_count}</span>
                                </div>
                                {analysis.resume_data.summary && (
                                    <div className="mt-3">
                                        <span className="text-gray-600">Summary:</span>
                                        <p className="text-gray-800 mt-1 text-xs leading-relaxed">
                                            {analysis.resume_data.summary}
                                        </p>
                                    </div>
                                )}
                            </div>
                        </div>

                        {/* Job Summary */}
                        <div className="p-4 bg-gray-50 rounded-lg">
                            <h3 className="text-lg font-semibold text-gray-800 mb-3">Job Summary</h3>
                            <div className="space-y-2 text-sm">
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Position:</span>
                                    <span className="font-medium">{analysis.job_data.job_title || 'Not specified'}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Company:</span>
                                    <span className="font-medium">{analysis.job_data.company_name || 'Not specified'}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Required Skills:</span>
                                    <span className="font-medium">{analysis.job_data.required_skills_count}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Job Type:</span>
                                    <span className="font-medium">{analysis.job_data.job_type}</span>
                                </div>
                                <div className="flex justify-between">
                                    <span className="text-gray-600">Experience Level:</span>
                                    <span className="font-medium">{analysis.job_data.experience_level}</span>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CoverLetterGenerator;
